function displaySelectedImage(e,t){let r=document.getElementById(t),o=e.target;if(o.files&&o.files[0]){let i=new FileReader;i.onload=function(e){r.src=e.target.result},i.readAsDataURL(o.files[0])}}const image_upload_form=document.getElementById("image-upload");image_upload_form.addEventListener("submit",e=>{e.preventDefault();let t=document.getElementById("picture"),r=new FormData(image_upload_form);for(let[o,i]of r.entries())r.set(o,DOMPurify.sanitize(i));r.append("picture",t.files[0]),fetch("/admin/upload-image",{method:"POST",body:r}).then(e=>e.json()).then(e=>{e.success?Swal.fire({title:"Success",text:"Blog Uploaded Successfully",icon:"success",confirmButtonColor:"#f87c00"}):Swal.fire({title:"Error",text:e.message,icon:"error",confirmButtonColor:"#f87c00"})}).catch(e=>{console.error(e),Swal.fire({title:"Error",text:"Internel Server Error",icon:"error",confirmButtonColor:"#f87c00"})})});