const reveiw_form=document.getElementById("reveiw"),id_input=document.getElementById("user-id");reveiw_form.addEventListener("submit",e=>{e.preventDefault();let r=document.querySelector('input[name="feedback"]:checked'),t=id_input.value,o=new FormData(reveiw_form);for(let[i,n]of(o.set("feedback",r.value),o.entries()))o.set(i,DOMPurify.sanitize(n));let c=confirm("Are you sure you want to send ?");c&&fetch(`/submit-reveiw/${t}`,{method:"POST",body:o}).then(e=>e.json()).then(e=>{e.success?Swal.fire({title:"Success",text:"Blog Uploaded Successfully",icon:"success",confirmButtonColor:"#f87c00"}):Swal.fire({title:"Error",text:e.message,icon:"error",confirmButtonColor:"#f87c00"})}).catch(e=>{console.error(e),Swal.fire({title:"Error",text:"Internel Server Error",icon:"error",confirmButtonColor:"#f87c00"})})});